import React, { useState, useEffect } from "react";
import { openDB } from "idb";

function TestSix() {
  const [text, settext] = useState("");
  const [file, setfile] = useState("");
  const [arr, setarr] = useState([]);
  let db;

  const request = indexedDB.open("imgDnD");
  request.onsuccess = function () {
    db = request.result;
    // console.log(db);
  };

  request.onupgradeneeded = (e) => {
    let db = e.target.result;
    let dbStore = db.createObjectStore("book", {
      autoIncrement: true,
    });
  };

  const addData = (e) => {
    e.preventDefault();
    let data = {
      imagesName: text,
      // imageFile: file,
    };
    let transaction = db.transaction(["book"], "readwrite");

    let dbStore = transaction.objectStore("book");

    console.log(dbStore);
    dbStore.add(data);
  };

  console.log(arr);

  // console.log(imgArr)
  // console.log(i);
  // console.log(imgArr[0])

  const showData = () => {
    console.log()
    const request = db.transaction("book").objectStore("book").getAll();

    request.onsuccess = () => {
      const book = request.result;

      console.log(book);
      console.table(book);
      setarr(book);
    };

    request.onerror = (err) => {
      console.error(`Error to get all students: ${err}`);
    };
  };


  const deleteData=(e)=>{
    console.log(db)
    const request = db.transaction('book').objectStore('book').delete(e);
    request.onsuccess = ()=> {
      console.log(`Student deleted, name: ${request.result}`);
  }
  }

  return (
    <>
      <div>
        <div
          style={{
            width: "200px ",
            height: "280px",
            padding: "5px",
            margin: "15px",
          }}
        >
          <form
            style={{
              width: "100%",
              height: "200px",
              borderRadius: "5px",
              border: "2px dashed",
              color: "red",
              fontSize: "0.9rem",
              fontWeight: "500",
              display: "flex",
              justifyContent: "center",
              alignItem: "center",
              flexDirection: "column",
            }}
          >
            <input
              type="text"
              value={text}
              onChange={(e) => settext(e.target.value)}
              placeholder="Enter Image lable"
            ></input>

            <span
              style={{
                backgroundColor: "lightGrey",
                padding: "2%",
                marginTop: "20px",
              }}
            >
              {/* <input
                style={{
                  backgroundColor: "lightGrey",
                  height: "80px",
                  width: "100%",
                  marginTop: "35px",
                }}
                type="file"
                accept="image/*"
                multiple
                onChange={(e) => setfile((e.target.files))}
              ></input> */}
            </span>
            <br />
            <br />
            <button style={{ marginTop: "15px" }} onClick={addData}>
              add data
            </button>
            <button type="button" onClick={showData}>
              get Data{" "}
            </button>

            <button>Clear All Data</button>
          </form>
        </div>

        {/* SHOW DATA DIV */}

        <div
          style={{
            width: "100%",
            display: "flex",
            justifyContent: "flex-start",
            alignItem: "flex-start",
            flexWrap: "wrap",
            position: "relative",
            height: "auto",
            marginTop: "20px",
            maxHeight: "300px",
            overflowY: "auto",
          }}
        >
          {/* <img src={arr[0]} alt="img" width="100" height="50" /> */}
          <ul>{
            arr.map((ele,i)=>{
              return(
                <>
                <div key={i}>

                  <li >{ele.imagesName}</li>
                  <span style={{cursor:"pointer"}} onClick={deleteData(i)}>X</span>
                </div>
                </>
              )
            })
          }</ul>
        </div>
      </div>
    </>
  );
}

export default TestSix;
