import React from "react";
import { useState, useEffect } from "react";

function TestTwo() {
  const [text, settext] = useState();

  const [file, setfile] = useState();

  let db;
  let store;

  useEffect(() => {
    window.indexedDB =
      window.indexedDB ||
      window.mozIndexedDB ||
      window.webkitIndexedDB ||
      window.msIndexedDB;
    // DON'T use "var indexedDB = ..." if you're not in a function.
    // Moreover, you may need references to some window.IDB* objects:
    window.IDBTransaction =
      window.IDBTransaction ||
      window.webkitIDBTransaction ||
      window.msIDBTransaction;
    window.IDBKeyRange =
      window.IDBKeyRange || window.webkitIDBKeyRange || window.msIDBKeyRange;

    // open db

    const DBOpenRequest = window.indexedDB.open("imgDB");

    // DBOpenRequest.onerror = function (event) {
    //   alert("Error loading database.");
    // };

    DBOpenRequest.onsuccess = function (event) {
      alert("Database initialised.");

     
      db = DBOpenRequest.result;
      console.log(db);

      store = db.createObjectStore("imageDB", {
        autoIncrement: true,
      })

      // Run the displayData() function to populate the task list with all the to-do list data already in the IDB
    //   displayData();
    };
  });

  const addData=()=>{

  }

  return (
    <>
      <div>
        <div style={{ width: "150px ", height: "250px", padding: "5px" }}>
          <form>
            <input
              type="text"
              value={text}
              onChange={(e) => settext(e.target.value)}
              placeholder="Enter Image lable"
            ></input>

            <span
              style={{
                backgroundColor: "lightGrey",
                padding: "2%",
                marginTop: "20px",
              }}
            >
              <input
                style={{
                  backgroundColor: "lightGrey",
                  height: "10%",
                  width: "100%",
                }}
                type="file"
                // value={file}
                multiple
                onChange={(e) => setfile(e.target.value)}
              ></input>
            </span>
            <br />
            <br />
            <button onClick={addData}>add data</button>
          </form>
        </div>

        {/* SHOW DATA DIV */}

        <div>
          <img src="" alt="img" width="100" height="50" />
        </div>
      </div>
    </>
  );
}

export default TestTwo;
