import React, { useState, useEffect } from "react";
import { openDB } from "idb";

function TestSix() {
  const [text, settext] = useState("");
  const [file, setfile] = useState("");
  const [arr, setarr] = useState([]);
  // const [image, setimage] = useState();
  let db;

  const request = indexedDB.open("imgDnD");
  request.onsuccess = function () {
    db = request.result;
    // console.log(db);
  };

  request.onupgradeneeded = (e) => {
    let db = e.target.result;
    let dbStore = db.createObjectStore("book", {
      keyPath: "imageFile",
    });
  };

  const addData = (e) => {
    e.preventDefault();
    let data = {
      imagesName: text,
      imageFile: file,
    };
    let transaction = db.transaction(["book"], "readwrite");

    let dbStore = transaction.objectStore("book");

    console.log(dbStore);
    dbStore.add(data);
  };

  console.log(arr);

  // console.log(imgArr)
  // console.log(i);
  // console.log(imgArr[0])

  const showData = (e) => {
    e.preventDefault();
    console.log(db);
    const request = db.transaction("book").objectStore("book").getAll();

    request.onsuccess = () => {
      const book = request.result;

      console.log(book);
      console.table(book);
      setarr(book);
    };

    request.onerror = (err) => {
      console.error(`Error to get all students: ${err}`);
    };
  };

  const DeleteAll = (e) => {
    e.preventDefault();
    console.log(db);
    const request = db
      .transaction("book", "readwrite")
      .objectStore("book")
      .clear();

    request.onsuccess = () => {
      console.log(`Object Store "${"book"}" emptied`);
    };
    request.onerror = (err) => {
      console.error(`Error to empty Object Store: ${"book"}`);
    };
  };

  request.onsuccess = function (event) 
{
    db = request.result;
    console.log('The database is opened successfully');

     // Add this
};

   function deleteData  (imageFile) {
    console.log(db);
    const request = db.transaction('book','readwrite')
    .objectStore('book')
    .delete(imageFile);
  };

  setTimeout()

  const setImageOnChange = (e) => {
    const [file] = e.target.files;
    setfile(URL.createObjectURL(file));
  };

  return (
    <>
      <div>
        <div
          style={{
            width: "200px ",
            height: "280px",
            padding: "5px",
            margin: "15px",
          }}
        >
          <form
            style={{
              width: "100%",
              height: "200px",
              borderRadius: "5px",
              border: "2px dashed",
              color: "red",
              // fontSize: "0.9rem",
              // fontWeight: "500",
              display: "flex",
              justifyContent: "center",
              alignItem: "center",
              flexDirection: "column",
            }}
          >
            <input
              type="text"
              value={text}
              onChange={(e) => settext(e.target.value)}
              placeholder="Enter Image lable"
            ></input>

            <span
              style={{
                backgroundColor: "lightGrey",
                padding: "2%",
                marginTop: "20px",
              }}
            >
              <input
                style={{
                  backgroundColor: "lightGrey",
                  height: "40px",
                  width: "100%",
                  marginTop: "2px",
                }}
                type="file"
                accept="image/*"
                multiple
                onChange={setImageOnChange}
              ></input>
            </span>
            <br />
            <br />
            <button style={{ marginTop: "15px" }} onClick={addData}>
              Add data
            </button>
            <button type="button" onClick={showData}>
              Show Data{" "}
            </button>

            <button onClick={DeleteAll}>Clear All Data</button>
          </form>
        </div>

        {/* SHOW DATA DIV */}

        <div
          style={{
            width: "100%",
            display: "flex",
            justifyContent: "flex-start",
            alignItem: "flex-start",
            flexWrap: "wrap",
            position: "relative",
            height: "auto",
            marginTop: "20px",
            maxHeight: "300px",
            overflowY: "auto",
          }}
        >
          <ul>
            {arr.map((ele, i) => {
              return (
                <>
                  <div key={i}>
                    <button type="button" onClick={deleteData(ele.imageFile)}>
                      X
                    </button>
                    <span>{ele.imagesName}</span>
                    <li>
                      <img
                        src={ele.imageFile}
                        alt="img"
                        width="100"
                        height="50"
                      />
                    </li>
                  </div>
                </>
              );
            })}
          </ul>
        </div>
      </div>
    </>
  );
}

export default TestSix;
